import CryptoJS from "crypto-js";

const LIVE_URL = "https://healthservice.priaid.ch";
const AUTH_URL = "https://authservice.priaid.ch/login";

// Replace these with your actual credentials
const API_KEY = "Dr3k4K6EnGe2m5LNt";
const API_SECRET = "Cr6f9_UFL_EDU_AUT";

const getToken = async () => {
  try {
    console.log("Attempting to get token...");

    // Create proper authentication hash
    const computedHash = CryptoJS.HmacMD5(AUTH_URL, API_SECRET);
    const computedHashString = computedHash.toString(CryptoJS.enc.Base64);

    const response = await fetch(AUTH_URL, {
      method: "POST",
      headers: {
        Authorization: `Bearer ${API_KEY}:${computedHashString}`,
        "Content-Type": "application/json",
      },
      // Add mode and credentials
      mode: "cors",
      credentials: "omit",
    });

    if (!response.ok) {
      const errorText = await response.text();
      console.error("Authentication failed:", errorText);
      throw new Error(`Authentication failed: ${response.status}`);
    }

    const data = await response.json();
    console.log("Token received successfully");
    return data.Token;
  } catch (error) {
    console.error("Detailed auth error:", error);
    throw error;
  }
};

export const medicApi = {
  getSymptoms: async () => {
    try {
      const token = await getToken();
      const response = await fetch(
        `${LIVE_URL}/symptoms?token=${token}&format=json&language=en-gb`,
        {
          headers: {
            Accept: "application/json",
            "Cache-Control": "no-cache",
          },
        }
      );

      if (!response.ok) {
        throw new Error("Failed to fetch symptoms");
      }

      return await response.json();
    } catch (error) {
      console.error("Error fetching symptoms:", error);
      throw error;
    }
  },

  getDiagnosis: async (symptoms, gender, yearOfBirth) => {
    try {
      const token = await getToken();
      const response = await fetch(
        `${LIVE_URL}/diagnosis?symptoms=${JSON.stringify(
          symptoms
        )}&gender=${gender}&year_of_birth=${yearOfBirth}&token=${token}&format=json&language=en-gb`,
        {
          headers: {
            Accept: "application/json",
            "Cache-Control": "no-cache",
          },
        }
      );

      if (!response.ok) {
        throw new Error("Failed to get diagnosis");
      }

      return await response.json();
    } catch (error) {
      console.error("Error getting diagnosis:", error);
      throw error;
    }
  },

  getIssueInfo: async (issueId) => {
    try {
      const token = await getToken();
      const response = await fetch(
        `${LIVE_URL}/issues/${issueId}/info?token=${token}&format=json&language=en-gb`,
        {
          headers: {
            Accept: "application/json",
            "Cache-Control": "no-cache",
          },
        }
      );

      if (!response.ok) {
        throw new Error("Failed to get issue info");
      }

      return await response.json();
    } catch (error) {
      console.error("Error getting issue info:", error);
      throw error;
    }
  },
};
